            console.log(errors)
        }
    }, [errors]);

    return ( 
        <div>
        
        <form className="form">
       
        <h2>User Details</h2>
        <div className="input-group">
            <input 
            type="text" 
            placeholder="FirstName"
            required
            name="firstName"
            onChange={handleChange}
            value={values.firstName}
            />

        {/* <p>{err.firstName}</p> */}
            {/* <p>hello</p> */}
            {/* {valid && <small className="error"> {` is required`} </small>} */}
        </div>

        <div className="input-group">
            <input 
            type="text" 
            placeholder="LastName"
            required
            value={values.lastName}
            onChange={handleChange}
            name="lastName"
            />
             {/* <p>{err.lastName}</p> */}
           {/* {valid && <small className="error"> {` is required`} </small>} */}
        </div>

        <div className="input-group">
            <input 
            type="email" 
            placeholder="Email"
            required
            value={values.email}
            onChange={handleChange}
            name="email"
            />

        {/* <p>{err.email}</p> */}
          {/* {valid && <small className="error"> {` is required`} </small>} */}
        </div>

        <div className="input-group">
            <select
                value={values.gender}
                onChange={handleChange}
                name="gender"
            >
                <option value="">--chose your gender--</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="others">others</option>
            </select>
        </div>
            <button 
                onClick={cont}
                className='cont-btn'
                >
                Continue
            </button>
        </form>
        </div>
     );
}
 
