import { useState } from "react";

const UserDetails = ({ nextStep, handleChange, values}) => {
    const [valid, setValid] = useState(false)

    let cont = (e) => {
        e.preventDefault();
        console.log(values)

      if (values.firstName === '') {
          console.log(valid)
          setValid(true)
      } else {
          setValid(false)
      }
      if (values.lasttName === '') {
        setValid(true)
    } else {
        setValid(false)
    }
    if (values.email === '') {
        setValid(true)
    } else {
        setValid(false)
    }

    if (values.firstName !== '' && values.lastName !== '' && values.email !== '') {
        nextStep()
    }


        // if (invalid.length > 0) {
        //     setValid(true)
        // } else {
        //     nextStep()
        // }
       
    } 

    return ( 
        <div>
        
        <form className="form">
       
        <h2>User Details</h2>
        <div className="input-group">
            <input 
            type="text" 
            placeholder="FirstName"
            required
            name="firstName"
            onChange={handleChange}
            value={values.firstName}
            />
            <p>hello</p>
            {valid && <small className="error"> {` is required`} </small>}
        </div>

        <div className="input-group">
            <input 
            type="text" 
            placeholder="LastName"
            required
            value={values.lastName}
            onChange={handleChange}
            name="lastName"
            />
           {valid && <small className="error"> {` is required`} </small>}
        </div>

        <div className="input-group">
            <input 
            type="email" 
            placeholder="Email"
            required
            value={values.email}
            onChange={handleChange}
            name="email"
            />
          {valid && <small className="error"> {` is required`} </small>}
        </div>

        <select
            value={values.gender}
            onChange={handleChange}
            name="gender"
        >
            <option value="">--chose your gender--</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="others">others</option>
        </select>

            <button 
                onClick={cont}
                className='cont-btn'
                >
                Continue
            </button>
        </form>
        </div>
     );
}
 
export default UserDetails;
