import { useState } from 'react'
import UserDetails from './UserDetails'


const AllForms = () => {

    const [allForms, setAllForms] = useState({
        step: 1,
        firstName: '',
        lastName: '',
        email: '',
        gender: "Select your gender",
        phone: '',
        occupation: '',
        city: '',
        bio: '',
    })

    let nextStep = () => {
        const { step } = allForms;
        setAllForms({
            step: step + 1
        })
    }

    let preStep = () => {
        const { step } = allForms;
        setAllForms({
            step: step - 1
        })
    }

    let handleChange = (input) => (e) => {
        setAllForms({[input]: e.target.value})
    }

    const { step } = allForms;
    const { firstName, lastName, email, gender, phone, occupation, city, bio} = allForms
    const values = { firstName, lastName, email, gender, phone, occupation, city, bio}


        switch(step) {
            case 1:
              return (
                   <UserDetails 
               nextStep={nextStep}
               handleChange={handleChange}
               values={values}
               />
              )
               break;
            case 2: 
                return <h1>MoreDetails</h1>
                break;
            case 3: 
                return <h1>Confirm</h1>
            break;
            
        }
}
 
export default AllForms;
