const UserDetails = ({ nextStep, handleChange, values}) => {

    let erorMessage;

    let cont = (e) => {
        e.preventDefault();
        const invalid = values.filter(value => {
           return value.length < 1
        })
        if (invalid.length > 0) {
            erorMessage = <small className="error">{`${invalid} is required`}</small> 
        } else {
            nextStep()
        }
    } 

    return ( 
        <div>
        
        <form className="form">
       
        <h2>User Details</h2>
        <div className="input-group">
            <input 
            type="text" 
            placeholder="FirstName"
            required
            name="firstName"
            onChange={handleChange}
            value={values.firstName}
            />
            <p>hello</p>
            {erorMessage}
        </div>

        <div className="input-group">
            <input 
            type="text" 
            placeholder="LastName"
            required
            value={values.lastName}
            onChange={handleChange}
            name="lastName"
            />
            <small className="error">
            {erorMessage}
            </small>
        </div>

        <div className="input-group">
            <input 
            type="email" 
            placeholder="Email"
            required
            value={values.email}
            onChange={handleChange}
            name="email"
            />
            <small className="error">
            {erorMessage}
            </small>
        </div>

        <select
            value={values.gender}
            onChange={handleChange}
            name="gender"
        >
            <option value="">--chose your gender--</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="others">others</option>
        </select>

            <button 
                onClick={cont}
                className='cont-btn'
                >
                Continue
            </button>
        </form>
        </div>
     );
}
 
export default UserDetails;
