const UserDetails = ({ nextStep, handleChange, values}) => {
    
    let cont = (e) => {
        e.preventDefault();


        nextStep()
    } 

    return ( 
        <div>
        
        <form className="form">
        <h2>User Details</h2>
           <input 
           type="text" 
           placeholder="FirstName"
           required
           name="firstName"
           onChange={handleChange}
           value={values.firstName}
           />
            <input 
           type="text" 
           placeholder="LastName"
           required
           value={values.lastName}
           onChange={handleChange}
           name="lastName"
           />
            <input 
           type="email" 
           placeholder="Email"
           required
           value={values.email}
           onChange={handleChange}
           name="email"
           />
           <select
             value={values.gender}
             onChange={handleChange}
             name="gender"
           >
               <option value="">--chose your gender--</option>
               <option value="male">Male</option>
               <option value="female">Female</option>
               <option value="others">others</option>
           </select>

            <button 
           onClick={cont}
           className='cont-btn'
           >
               Continue</button>
        </form>
        </div>
     );
}
 
export default UserDetails;
